Select vtiger_users.id, vtiger_users.first_name, vtiger_users.last_name, vtiger_users.email1, vtiger_user2role.roleid, vtiger_role.rolename, vtiger_role2profile.profileid, vtiger_users.status, vtiger_users.user_name, vtiger_profile.profilename From vtiger_users Inner Join vtiger_user2role On vtiger_users.id = vtiger_user2role.userid Inner Join vtiger_role On vtiger_user2role.roleid = vtiger_role.roleid Inner Join vtiger_role2profile On vtiger_role.roleid = vtiger_role2profile.roleid Inner Join vtiger_profile On vtiger_role2profile.profileid = vtiger_profile.profileid
